INSERT INTO /*{VIVVO_DB_PREFIX}*/articles (`id`, `category_id`, `user_id`, `user_domain`, `author`, `title`, `image`, `created`, `last_edited`, `body`, `last_read`, `times_read`, `today_read`, `status`, `sefriendly`, `link`, `order_num`, `show_poll`, `show_comment`, `rss_feed`, `keywords`, `description`, `emailed`, `vote_num`, `vote_sum`, `abstract`, `image_caption`, `video_attachment`) VALUES
(3000, 2, 1, 'vivvo@localhost', 'Vivvo CMS', 'Welcome!', 'vivvo_logo.gif', '2007-09-17 07:56:00', '2010-06-03 17:06:42', '<p>Bravo for you! :)</p><p>You can delete this article at any moment by logging into the admin and choosing Manage article options -&gt; delete.<br /><br />The Vivvo Team</p>', '2010-06-03 17:56:00', '12', '0', '1', 'welcome', '', '1', '1', '1', '1', NULL, NULL, '0', '2', '6', 'Congratulations! You have successfully installed and configured Vivvo.', 'Vivvo CMS - welcome', NULL);
INSERT INTO /*{VIVVO_DB_PREFIX}*/articles (`id`, `category_id`, `user_id`, `user_domain`, `author`, `title`, `image`, `created`, `last_edited`, `body`, `last_read`, `times_read`, `today_read`, `status`, `sefriendly`, `link`, `order_num`, `show_poll`, `show_comment`, `rss_feed`, `keywords`, `description`, `emailed`, `vote_num`, `vote_sum`, `abstract`, `image_caption`, `video_attachment`) VALUES
(3001, 2, 1, 'vivvo@localhost', 'Vivvo CMS', 'What is Vivvo CMS?', 'vivvo_logo.gif', '2010-04-29 14:51:00', '2010-06-03 17:26:37', '<p><strong>Vivvo CMS</strong> is a news, article and media publishing software that enables you to create a full website easily without any knowledge or experience with HTML, Web site design or management through the comfortable Ajax-based site structure management interface and high flexibility of design, content, and functionality integration.</p><p>It gives you the power to create different layouts and different styles for each section of the site, different user groups to help you manage your content with a built-in privilege system, and above all, the ultimate Search Engine ranking capability.</p><p><span style=\"font-size: medium;\"><strong>Features &amp; Requirements</strong></span></p><p><strong><span style=\"color: #ff0000;\">Content Publishing</span></strong></p><p>1. Ability to publish Articles, Static Pages and Blogs</p><p>2. Ability to publish Image/Photo Galleries</p><p>3. Keeping revisions and auto-drafts of articles</p><p><span style=\"color: #000000;\">View more...</span></p><p><strong><span style=\"color: #ff0000;\">Search Engine Optimisation</span></strong></p><p>1. 3 different formats of SE Friendly URLs</p><p>2. Fully SEO optimized templates</p><p>3. Custom META keywords/description tags</p><p><span style=\"color: #000000;\"></span>View more...</p><p><strong><span style=\"color: #ff0000;\">Users and Roles</span></strong></p><p>1. Unlimited number of administrators and roles</p><p>2. 5 default roles: Admin, Editor, Trusted Writer, Writer, Member</p><p>3. Bridges to integrate users with most popular forum software  like IPB or vBulletin</p><p><span style=\"color: #000000;\"></span>View more...</p><p><strong><span style=\"color: #ff0000;\">Documents &amp; Media Management</span></strong></p><p>1. Robust DMS system for managing documents and assets</p><p>2. Ability to tag files</p><p>3. Full media streaming capabilities</p><p><span style=\"color: #000000;\"></span>View more...</p><p><strong><span style=\"color: #ff0000;\"> Templates and Design</span></strong></p><p>1. Designer-friendly template engine (VTE)</p><p>2. 20+ free skins/templates to start with</p><p>3. 40+ individual page layouts to choose</p><p><span style=\"color: #000000;\"></span>View more...</p><p><strong><span style=\"color: #ff0000;\">Plug-ins and Extensions</span></strong></p><p>1. 100+ free plug-ins and widgets to start with</p><p>2. Easily extensible through a plug-in architecture</p><p>3. Helpers, Docs and Plugin Generator Wizard available to 3rd  party developers</p><p><span style=\"color: #000000;\"></span>View more...</p>', '2010-06-03 17:56:01', '19', '0', '1', 'vivvo_article_manager', '', '2', '1', '1', '1', NULL, NULL, '0', '1', '4', 'VIVVO is a news Content Management System atop a powerful framework, empowering thousands of industry leading online newspapers, magazines, portals and media agencies.', 'Vivvo Article Manager', NULL);
INSERT INTO /*{VIVVO_DB_PREFIX}*/articles (`id`, `category_id`, `user_id`, `user_domain`, `author`, `title`, `image`, `created`, `last_edited`, `body`, `last_read`, `times_read`, `today_read`, `status`, `sefriendly`, `link`, `order_num`, `show_poll`, `show_comment`, `rss_feed`, `keywords`, `description`, `emailed`, `vote_num`, `vote_sum`, `abstract`, `image_caption`, `video_attachment`) VALUES
(3002, 2, 1, 'vivvo@localhost', 'Vivvo CMS', 'Getting Started', 'vivvo_logo.gif', '2010-04-29 14:51:00', '2010-06-03 17:44:52', '<p>Hi, and welcome to Vivvo 4.5! This is the place to get started with Vivvo CMS!</p><p>As a general guideline, we suggest taking the following steps to get the most out of our technical support service:</p><p>1. Online Documentation&nbsp;-&nbsp;Updated regularly with each version release, Online Documentation contains product-specific information - from instructions on how to install and get started, to detailed product features, tutorials and references.</p><p>2. Consult Our Knowledge Base&nbsp;-&nbsp;Need an answer fast? Browse our knowledgebase for articles on a broad range of topics.</p><p>3. Participate in Discussion Forums&nbsp;-&nbsp;Our support Forums allow users to exchange valuable information with other users and developers on general technical issues and product-specific topics</p><p>4. Submit a Support Request&nbsp;-&nbsp;Submitting your support questions through the online support form is the best way to ensure that your requests are properly tracked and answered by our technical support team. Each license level is assigned the appropriate number of support incidents, and an upgraded support package can be obtained if necessary.</p>', '2010-06-03 17:56:01', '17', '0', '1', 'getting_started', '', '3', '1', '1', '1', NULL, NULL, '0', '0', '0', 'Our Support Center includes comprehensive resources to address your technical questions. Our technical support team is committed to providing superior support service.', 'Getting started with Vivvo CMS', NULL);
INSERT INTO /*{VIVVO_DB_PREFIX}*/articles (`id`, `category_id`, `user_id`, `user_domain`, `author`, `title`, `image`, `created`, `last_edited`, `body`, `last_read`, `times_read`, `today_read`, `status`, `sefriendly`, `link`, `order_num`, `show_poll`, `show_comment`, `rss_feed`, `keywords`, `description`, `emailed`, `vote_num`, `vote_sum`, `abstract`, `image_caption`, `video_attachment`) VALUES
(3003, 2, 1, 'vivvo@localhost', 'Vivvo CMS', 'Top Features', 'top_features.jpg', '2010-04-29 14:53:00', '2010-04-29 16:01:32', '<p>It\'s been awhile since our last major update to Vivvo CMS, but that\'s what happens when you\'re busy making great CMS even more awesome :) After one year of development we are ready to release Vivvo 4.5 to the world. Over 450 issues/tickets have been resolved during the release cycle. This release contains many improvements - some visible on the first glance, and others hidden beneath the hood. And here are the most important novelties, ones we\'re so excited about:</p><p>&nbsp;</p><h2><strong>1. Revisions &amp; Auto-draft</strong></h2><p><strong>Working with content is now elevated to a next level</strong>. You will be able to keep unlimited number of revisions (versions) of single article and revert to old revisions. Also, the system will now automatically keep <strong>auto-draft</strong> of every article.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=wn_revisions.jpg\" border=\"0\" /></p><p>&nbsp;</p><h2><strong>2. Google Analytics integration</strong></h2><p>Powerful integration between Vivvo and <strong>Google Analytics</strong> will allow you to set triggers and events in your GA account, and monitor article reads, comments, shares over time as well as to see all the data GA collects from your Vivvo Dashboard.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=wn_analytics.jpg\" border=\"0\" /></p><p>&nbsp;</p><h2><strong>3. Topics </strong></h2><p>With new concept of <strong>topics </strong>in Vivvo, you\'ll have the ability to provide semantic relations to your content, cross-post between sections and control homepage and other sectios of website with ease.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=getting_started.jpg\" border=\"0\" /></p><p>&nbsp;</p><h2><strong>4. Trashbin / Soft-delete</strong></h2><p>This important security improvement will not only keep articles from accidental deleting, but allow you to manage who has control over deleted articles and set automatic purge periods.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=wn_trashbin.jpg\" border=\"0\" /></p>', '2010-06-03 17:46:10', '16', '0', '1', 'you-told-us-what-you-wanted-in-vivvo-and-now-we-re-introducing-the-coolest-version-of-vivvo-ever', NULL, '5', '1', '1', '1', NULL, NULL, '0', '1', '5', 'You told us what you wanted in Vivvo. And now we&#039;re introducing the coolest version of Vivvo ever.', NULL, NULL);
INSERT INTO /*{VIVVO_DB_PREFIX}*/articles (`id`, `category_id`, `user_id`, `user_domain`, `author`, `title`, `image`, `created`, `last_edited`, `body`, `last_read`, `times_read`, `today_read`, `status`, `sefriendly`, `link`, `order_num`, `show_poll`, `show_comment`, `rss_feed`, `keywords`, `description`, `emailed`, `vote_num`, `vote_sum`, `abstract`, `image_caption`, `video_attachment`) VALUES
(3004, 2, 1, 'vivvo@localhost', 'Vivvo CMS', 'Refinements', 'refinements.jpg', '2010-04-29 15:20:00', '2010-04-29 16:01:52', '<h2>1. Navigation</h2><p>Navigation is now greatly improved, with all you need at just a single click and exciting ability to \"pin\" most needed plugins directly to main nav bar!</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=wn_navbar.jpg\" border=\"0\" /></p><h2>2. Dashboard</h2><p>Gain more control with less effort. With new Dashboard you will be able to grasp all important aspects of managing your website at a single glance - statistics, actions, user interaction and important account information.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=wn_dashboard.jpg\" border=\"0\" /></p><h2>3. Native support for Video</h2><p>Managing audio/video files, integrating YouTube and other sources and playing video is now seamlessly integrated in the system. You just won\'t believe how easy it can get.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=getting_started.jpg\" border=\"0\" /></p><h2>4. Comments</h2><p>Now, with new commenting system in Vivvo you\'ll be able to have all the features services like YouTube or CNN offer - threaded comments, replying, comment voting and subscribing, font styles and sharing.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=wn_comments.jpg\" border=\"0\" /></p><h2>5. Save+ Button</h2><p>Save+ Button will add incredible user experience to your content editing. Now you\'ll be able to choose action state of \"Save\" button and speed up working with content up to three times.</p><p><img src=\"/*{VIVVO_URL}*/files.php?file=wn_saveplus.jpg\" border=\"0\" /></p>', '2010-06-03 17:45:46', '15', '0', '1', 'refinements', NULL, '4', '1', '1', '1', NULL, NULL, '0', '1', '5', 'Redefined. Not reinvented.', NULL, NULL);
INSERT INTO /*{VIVVO_DB_PREFIX}*/articles (`id`, `category_id`, `user_id`, `user_domain`, `author`, `title`, `image`, `created`, `last_edited`, `body`, `last_read`, `times_read`, `today_read`, `status`, `sefriendly`, `link`, `order_num`, `show_poll`, `show_comment`, `rss_feed`, `keywords`, `description`, `emailed`, `vote_num`, `vote_sum`, `abstract`, `image_caption`, `video_attachment`) VALUES
(3005, 1, 1, 'vivvo@localhost', 'Vivvo CMS', 'Basic article creation video', 'article_creation_video.jpg', '2010-04-29 15:34:00', '2010-06-04 10:14:26', NULL, '2010-06-03 17:46:34', '4', '0', '1', 'basic-article-creation-video', NULL, '7', '1', '1', '1', NULL, NULL, '0', '0', '0', 'This video shows you how to create and publish an article in Vivvo. See how you can format your content, add images to the article&#039;s body with ease or set article preferences like status, publishing date or search friendly URL and metadata.', NULL, 'http://www.youtube.com/watch?v=FKg9YKLk4XU');
